/**
 * @desc
 * Also known as the "null function" or "**no op**eration", always
 * returns the value `undefined` and does absolutely nothing.
 * @example
 * map(noop)(Array(2)); //> [undefined, undefined]
 * noop(); //> undefined
 * @see https://en.wikipedia.org/wiki/Null_function
 * @return {undefined} - Has return type void.
 */
export function noop(): void {
  return undefined;
}

import {Producer} from "../";

/**
 * @desc
 * Runs a function a certain amount of times and returns an array
 * containing the return values of each invocation. It can be
 * useful when running functions that are impure (have side
 * effects) or produce mutable objects or arrays.
 * @example
 * // Simulated dice roll session (4 times)
 * repeatedly(() => randInt(6), 4); //> [2, 1, 3, 5]
 * @throws {Error}               - When the argument `times` is negative.
 * @throws {Error}               - When `times` is not a whole number.
 * @param {function(): T} effect - Callback with potential side effects and optional return value.
 * @param {number}        times  - Amount of times the callback will be run.
 * @return {T[]}                 - The collected results of each invocation.
 */
export function repeatedly<T>(effect: Producer<T>, times: number): T[] {
  if (times < 0) {
    throw new Error('[repeatedly]: Argument "times" must not be negative.');
  } else if (times % 1 !== 0) {
    throw new Error('[repeatedly]: Argument "times" must not be a decimal.');
  }

  const collectedResults: T[] = Array(times);

  for (let i = 0; i < times; i++) {
    collectedResults[i] = effect();
  }

  return collectedResults
}

/**
 * @desc
 * Curried function that first takes a function, then returns another
 * function that accepts `f`'s arguments and lastly, returns a
 * function that takes no arguments and returns the application
 * of the provided arguments to `f`. This is useful for lazy
 * evaluation and delaying the execution of the function.
 * @example
 * describe("myFunc", () => {
 *   it("should throw error", () => {
 *     expect(thunkF(myFunc)("bad argument"))
 *       .toThrowError(BadArgError);
 *   });
 * }); //! [PASS]
 *
 * thunkF(inc)(1)(); //> 2
 * @see {@link thunkV}
 * @param {function(args: ...any): any} f - A function to be called later.
 * @return {function(args: ...any): function(): any}
 * A function that takes no arguments and returns the result of calling f.
 */
export function thunkF<T extends any, A extends any[]>(f: (...args: A) => T) {
  return function thunkF__(...args: A) {
    return (): T => f(...args);
  }
}

/**
 * @desc
 * Takes any value or reference and returns a callable function
 * that takes no arguments and returns that very same value.
 * In functional programming this is called a **thunk**,
 * and it's very useful for lazy evaluation.
 * @example
 * const song = thunkV("Call me maybe...");
 *
 * song(); //> "Call me maybe..."
 * thunkV(true)(); //> true
 * @see {@link thunkF}
 * @see https://en.wikipedia.org/wiki/Thunk
 * @param {any} val          - The value to return later.
 * @return {function(): any} - The thunk that returns the value.
 */
export function thunkV<T>(val: T) {
  return (): T => val;
}

examples:
describe("myFunc", () => {
  it("should throw error", () => {
    expect(thunkF(myFunc)("bad argument"))
      .toThrowError(BadArgError);
  });
}); //! [PASS]

thunkF(inc)(1)(); //> 2

const song = thunkV("Call me maybe...");

song(); //> "Call me maybe..."
thunkV(true)(); //> true

