/**
 * @desc
 * Returns a function that keeps a number from being lower or
 * higher than the specified **boundaries**. This function is also
 * called `clamp` in other APIs and utility libraries. If you
 * only need to contain lower and upper limits separately, use
 * the partially applied versions of `max` and `min` instead.
 *
 * **Note**: The order of the two arguments is merely symbolic,
 * they will be flipped if `lower` is greater than `upper`.
 * @example
 * between(0, 10)(12); //> 10
 * between(-10, 0)(-15); //> -10
 * between(-1, 1)(0); //> 0
 * between(5, 5)(5); //> 5
 * @param {number} lower - Lower limit.
 * @param {number} upper - Upper limit.
 * @return {function(n: number): number}
 * Function that returns `lower` if `n < lower`, `upper` if
 * `n > upper` or just `n` otherwise.
 */
export function between(lower: number, upper: number) {
  return function between__(n: number): number {
    if (lower > upper) {
      [lower, upper] = [upper, lower];
    }

    return Math.min(upper, Math.max(lower, n));
  }
}

import {foldl} from "../";

/**
 * @desc
 * Takes a list of numbers and adds them all together.
 * An empty list will result in 0.
 * @example
 * sum(range(0, 10)); //> 55
 * sum([-1, 2, 3.5]); //> 4.5
 * sum([256]); //> 256
 * sum([]); //> 0
 * @see {@link product}
 * @param {number[]} numbers - The list of numbers to add together.
 * @return {number}          - The resulting sum.
 */
export function sum(numbers: number[]): number {
  return foldl((acc, n: number) => acc + n, 0)(numbers);
}

/**
 * @desc
 * Takes a list of numbers and multiplies them all together.
 * An empty list will result in 1.
 * @example
 * const factorial = (num: number) =>
 *   product(range(1, n));
 *
 * factorial(5); //> 120
 * product([10, 0, 5]); //> 0
 * product([5]); //> 5
 * product([]); //> 1
 * @see {@link sum}
 * @todo [Optimization] Make use of short-circuiting when it finds a 0.
 * @param {number[]} factors - The list of numbers to multiply.
 * @return {number}          - The resulting product.
 */
export function product(factors: number[]): number {
  const result = foldl((acc, n: number) => acc * n, 1)(factors);

  return result === -0 ? 0 : result;
}

