ord("10"); // 49
ord("");
//! Uncaught Error: [ord]: char
string must not be of length 0.

pipe(chars, map(ord))("");
//> [55357, 56840]

chr(230); //> 
chr(65); //> A

const joy = [
  108, 105, 116, 32,
  55357, 56834 // emoji
];

pipe(map(chr), unchars)(joy);
//> "lit "

